<?php////abstract class C_Base extends C_Controller {	protected $title;		// заголовок	protected $mainTitle;		// заголовок страницы	protected $content;		// основное содержимое	protected $headcontent;		// содержимое шапки с логином	protected $head;		// наименование файла шаблона шапки с логином	protected $mainMenu;		// наименование файла шаблона шапки с логином	protected $login;		// логин	protected $pageName;		// имя заголовка	public function __construct($head, $mainMenu, $login) {       $this->head = $head;       $this->mainMenu = $mainMenu;       $this->login = $login;   }		protected function before()	{		$this->title = '';		$this->mainTitle = 'Allsmarts';		$this->content = '';		$this->headcontent = '';	}	// проверка на логин админа	protected function checkAdmin()	{		if ($this->login === 'admin') {			return true;		}		return false;	}			public function render()	{		$vars = array(			'mainTitle' => $this->mainTitle, 			'head' => $this->headcontent,			'title' => $this->title,			'linkupdir' => '',			'menu' => showMenu($this->mainMenu),			'content' => $this->content,			'pagename' => $this->pageName,		);			$page = $this->Template(TEMPLATES_DIR . 'index.tpl', $vars);						echo $page;	}	}